import React, { useState, useEffect } from "react";
import PostService from "../../../API/PostService";
import Loader from "../../ui/loader/Loader";

const PostPage = ({ match }) => {
  const [post, setPost] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    title: "",
    subtitle: "",
    body: "",
    picture: null,
  });

  useEffect(() => {
    const fetchPost = async () => {
      try {
        const fetchedPost = await PostService.getById(match.params.id);
        setPost(fetchedPost);
        setFormData({
          title: fetchedPost.title,
          subtitle: fetchedPost.subtitle,
          body: fetchedPost.body,
          picture: fetchedPost.picture,
        });
        setIsLoading(false);
      } catch (error) {
        console.error("Ошибка при загрузке поста:", error);
      }
    };

    fetchPost();
  }, [match.params.id]);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData({
      ...formData,
      [name]: files ? files[0] : value,
    });
  };

  const handleEditToggle = () => {
    setIsEditing(!isEditing);
  };

  const handleUpdatePost = async () => {
    try {
      const updatedPost = await PostService.updatePost(post.id, formData);
      setPost(updatedPost);
      setIsEditing(false);
    } catch (error) {
      console.error("Ошибка при обновлении поста:", error);
    }
  };

  if (isLoading) {
    return <Loader />;
  }

  return (
    <div>
      {isEditing ? (
        <div>
          <h2>Редактировать пост</h2>
          <input
            type='text'
            name='title'
            value={formData.title}
            onChange={handleChange}
            placeholder='Заголовок'
          />
          <input
            type='text'
            name='subtitle'
            value={formData.subtitle}
            onChange={handleChange}
            placeholder='Подзаголовок'
          />
          <textarea
            name='body'
            value={formData.body}
            onChange={handleChange}
            placeholder='Содержимое'
          />
          <input type='file' name='picture' onChange={handleChange} />
          <button onClick={handleUpdatePost}>Сохранить изменения</button>
          <button onClick={handleEditToggle}>Отменить</button>
        </div>
      ) : (
        <div>
          <h2>{post.title}</h2>
          <h3>{post.subtitle}</h3>
          <p>{post.body}</p>
          {post.picture && <img src={post.picture} alt={post.title} />}
          <button onClick={handleEditToggle}>Редактировать пост</button>
        </div>
      )}
    </div>
  );
};

export default PostPage;
