import React, { useState, useMemo, useEffect, useContext } from "react";
import PostList from "../../ui/postform/PostList";
import PostForm from "../../ui/postform/PostForm";
import PostFilter from "../../ui/postform/PostFilter";
import MyModal from "../../ui/modal/MyModal";
import MyButton from "../../ui/button/MyButton";
import { usePosts } from "../../hooks/usePosts";
import PostService from "../../../API/PostService";
import Loader from "../../ui/loader/Loader";
import { useFetching } from "../../hooks/useFetching";
import { getPageCount } from "../../../utils/pages";
import Pagination from "../../ui/pagination/Pagination";
import { AuthContext } from "../../../context";

function Events() {
  const { isAuth } = useContext(AuthContext);
  const [allPosts, setAllPosts] = useState([]); // All posts
  const [filteredPosts, setFilteredPosts] = useState([]); // New state for filtered posts
  const [filter, setFilter] = useState({ sort: "", query: "" });
  const [totalPages, setTotalPages] = useState(0);
  const [limit, setLimit] = useState(5); // Items per page
  const [page, setPage] = useState(1);
  const [modal, setModal] = useState(false);

  const [fetchPosts, arePostsLoading, postError] = useFetching(async () => {
    const response = await PostService.getAll();
    setAllPosts(response.posts); // Fetch and store all posts
    setTotalPages(getPageCount(response.totalCount, limit)); // Calculate total pages based on initial posts
  });

  useEffect(() => {
    fetchPosts();
  }, []);

  // This useEffect is to handle filtering and pagination on search
  useEffect(() => {
    const sortedAndSearchedPosts = usePosts(
      allPosts,
      filter.sort,
      filter.query
    ); // Filtered posts
    setFilteredPosts(sortedAndSearchedPosts); // Store filtered posts
    setTotalPages(getPageCount(sortedAndSearchedPosts.length, limit)); // Update total pages based on search results
    setPage(1); // Reset to page 1 after a new search
  }, [filter, allPosts, limit]);

  const createPost = async (newPost) => {
    try {
      await PostService.createPost(newPost); // Create a new post
      setModal(false); // Close modal
      fetchPosts(); // Fetch posts again
    } catch (error) {
      console.error("Error creating post:", error);
    }
  };

  const removePost = async (post) => {
    try {
      await PostService.deletePost(post.id); // Delete the post
      setAllPosts(allPosts.filter((p) => p.id !== post.id)); // Update state after deletion
    } catch (error) {
      console.error("Ошибка при удалении поста:", error);
    }
  };

  const changePage = (newPage) => {
    setPage(newPage);
  };

  // Get current posts based on pagination (limit and page)
  const paginatedPosts = useMemo(() => {
    const startIndex = (page - 1) * limit;
    const endIndex = startIndex + limit;
    return filteredPosts.slice(startIndex, endIndex);
  }, [filteredPosts, page, limit]);

  return (
    <div className='events'>
      <div className='container'>
        <div>
          {isAuth && (
            <div>
              <MyButton onClick={fetchPosts}>Запросить посты</MyButton>
              <MyButton onClick={() => setModal(true)}>
                Создать новость
              </MyButton>
              <MyModal visible={modal} setVisible={setModal}>
                <PostForm create={createPost} />
              </MyModal>
            </div>
          )}

          <PostFilter filter={filter} setFilter={setFilter} />

          {postError && <h1>Произошла ошибка {postError}</h1>}

          {arePostsLoading ? (
            <div className='loading'>
              <Loader />
            </div>
          ) : (
            <PostList
              remove={removePost}
              posts={paginatedPosts} // Display only the paginated posts
              title={"Новости и мероприятия"}
            />
          )}

          <div className='pag'>
            <Pagination
              page={page}
              changePage={changePage}
              totalPages={totalPages} // Pagination based on filtered posts
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export default Events;
