import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import PostService from "../../../API/PostService";
import { useFetching } from "../../hooks/useFetching";
import Loader from "../../ui/loader/Loader";

const PostPage = () => {
  const params = useParams();
  const [post, setPost] = useState({});
  const [images, setImage] = useState([]);

  const [fetchPostById, isLoading, error] = useFetching(async (id) => {
    const response = await PostService.getById(id);
    setPost(response.data);
  });
  const [fetchImgsById, isImgLoading, imgError] = useFetching(async (id) => {
    const response = await PostService.getImageById(id);
    setImage(response.data);
  });

  useEffect(() => {
    fetchPostById(params.id);
    fetchImgsById(params.id);
  }, []);
  return (
    <div>
      <h1>Страница с новостью ID = {params.id}</h1>
      {isLoading ? (
        <Loader />
      ) : (
        <div>
          {post.id}. {post.title}. {post.body}
        </div>
      )}
      {error && <p>Error: {error}</p>}
      {isImgLoading ? <Loader /> : <div>{images.url}</div>}
    </div>
  );
};

export default PostPage;
