import React, { useMemo, useEffect, useState } from "react";
import "./pagination.css";
import { getPageArray } from "../../../utils/pages";

const Pagination = ({ totalPages }) => {
  const [currentPage, setCurrentPage] = useState(() => {
    const savedPage = localStorage.getItem("currentPage");
    return savedPage ? JSON.parse(savedPage) : 1; // Default to page 1 if no saved page
  });

  const pagesArray = useMemo(() => getPageArray(totalPages), [totalPages]);

  useEffect(() => {
    localStorage.setItem("currentPage", JSON.stringify(currentPage)); // Save current page to local storage
  }, [currentPage]);

  const changePage = (page) => {
    setCurrentPage(page);
  };

  return (
    <div className='page__wrapper'>
      {pagesArray.map((p) => (
        <span
          onClick={() => changePage(p)}
          key={p}
          className={currentPage === p ? "page page__current" : "page"}
        >
          {p}
        </span>
      ))}
    </div>
  );
};

export default Pagination;
