import React, { useState } from "react"; // Added useState import
import MyButton from "../button/MyButton";
import MyInput from "../input/MyInput";

const PostForm = ({ create }) => {
  const [post, setPost] = useState({
    title: "",
    description: "",
    body: "",
    image: null, // Change to null for file handling
  });

  const addNewPost = async (e) => {
    e.preventDefault();
    try {
      console.log("Creating new post with data:", post); // Log post data

      const formData = new FormData();
      formData.append("title", post.title);
      formData.append("description", post.description);
      formData.append("body", post.body);
      if (post.image) {
        formData.append("image", post.image); // Append the image file only if it exists
        console.log("Image added to form data:", post.image); // Log image file
      }

      console.log("Form data prepared for submission:", formData); // Log form data
      // Передаем новый пост через create функцию
      await create(formData);
      console.log("Post created successfully"); // Log success message

      // Сбрасываем поля формы
      setPost({ title: "", description: "", body: "", image: null });
    } catch (error) {
      console.error("Ошибка при создании поста:", error);
    }
  };

  return (
    <form onSubmit={addNewPost}>
      <MyInput
        value={post.title}
        onChange={(e) => {
          console.log("Title input changed:", e.target.value); // Log title change
          setPost({ ...post, title: e.target.value });
        }}
        type='text'
        placeholder='Заголовок новости'
      />
      <MyInput
        value={post.description}
        onChange={(e) => {
          console.log("Description input changed:", e.target.value); // Log description change
          setPost({ ...post, description: e.target.value });
        }}
        type='text'
        placeholder='Подзаголовок новости'
      />
      <MyInput
        value={post.body}
        onChange={(e) => {
          console.log("Body input changed:", e.target.value); // Log body change
          setPost({ ...post, body: e.target.value });
        }}
        type='text'
        placeholder='Содержание новости'
      />
      <input
        type='file'
        accept='image/*'
        onChange={(e) => {
          console.log("Image file selected:", e.target.files[0]); // Log selected image
          setPost({ ...post, image: e.target.files[0] }); // Set the file directly
        }}
      />
      <MyButton type='submit'>Создать новость</MyButton>
    </form>
  );
};

export default PostForm;
