import React, { useState, useEffect, useContext, useMemo } from "react";
import "./events.css";
import PostList from "../../ui/postform/PostList";
import PostForm from "../../ui/postform/PostForm";
import PostFilter from "../../ui/postform/PostFilter";
import MyModal from "../../ui/modal/MyModal";
import MyButton from "../../ui/button/MyButton";
import { usePosts } from "../../hooks/usePosts";
import PostService from "../../../API/PostService";
import Loader from "../../ui/loader/Loader";
import { useFetching } from "../../hooks/useFetching";
import { getPageCount } from "../../../utils/pages";
import Pagination from "../../ui/pagination/Pagination";
import { AuthContext } from "../../../context";

function Events() {
  const { isAuth } = useContext(AuthContext);
  const [allPosts, setAllPosts] = useState([]);
  const [filter, setFilter] = useState({ sort: "", query: "" });
  const [totalPages, setTotalPages] = useState(0);
  const [limit, setLimit] = useState(5);
  const [page, setPage] = useState(() => {
    const savedPage = localStorage.getItem("currentPage");
    return savedPage ? JSON.parse(savedPage) : 1;
  });
  const [modal, setModal] = useState(false);

  const [fetchPosts, arePostsLoading, postError] = useFetching(async () => {
    const response = await PostService.getAll(); // Загружаем все посты
    setAllPosts(response.posts);
  });

  useEffect(() => {
    fetchPosts();
  }, []);

  useEffect(() => {
    localStorage.setItem("currentPage", JSON.stringify(page));
  }, [page]);

  const sortedAndSearchedPosts = useMemo(() => {
    let sortedPosts = [...allPosts];

    if (filter.sort) {
      sortedPosts = sortedPosts.sort((a, b) =>
        a[filter.sort].localeCompare(b[filter.sort])
      );
    }

    if (filter.query) {
      sortedPosts = sortedPosts.filter((post) =>
        post.title.toLowerCase().includes(filter.query.toLowerCase())
      );
    }

    return sortedPosts;
  }, [filter.sort, filter.query, allPosts]);

  useEffect(() => {
    const totalCount = sortedAndSearchedPosts.length;
    setTotalPages(getPageCount(totalCount, limit));

    if (page > Math.ceil(totalCount / limit)) {
      setPage(1); // Сбрасываем на первую страницу, если текущая выходит за пределы
    }
  }, [sortedAndSearchedPosts, limit, page]);

  const createPost = async (newPost) => {
    try {
      await PostService.createPost(newPost);
      setModal(false);
      fetchPosts(); // Обновляем список постов после создания
    } catch (error) {
      console.error("Error creating post:", error);
    }
  };

  const removePost = async (post) => {
    try {
      await PostService.deletePost(post.id);
      setAllPosts(allPosts.filter((p) => p.id !== post.id));
    } catch (error) {
      console.error("Ошибка при удалении поста:", error);
    }
  };

  const changePage = (newPage) => {
    setPage(newPage);
  };

  const paginatedPosts = useMemo(() => {
    const start = (page - 1) * limit;
    const end = start + limit;
    return sortedAndSearchedPosts.slice(start, end);
  }, [page, limit, sortedAndSearchedPosts]);

  return (
    <div className='events'>
      <div className='container'>
        <div>
          {isAuth && (
            <div>
              <MyButton onClick={fetchPosts}>Запросить посты</MyButton>
              <MyButton onClick={() => setModal(true)}>
                Создать новость
              </MyButton>
              <MyModal visible={modal} setVisible={setModal}>
                <PostForm create={createPost} />
              </MyModal>
            </div>
          )}

          <PostFilter filter={filter} setFilter={setFilter} />
          {postError && <h1>Произошла ошибка {postError}</h1>}
          {arePostsLoading ? (
            <div className='loading'>
              <Loader />
            </div>
          ) : (
            <PostList
              remove={removePost}
              posts={paginatedPosts}
              title={"Новости и мероприятия"}
            />
          )}
          <div className='pag'>
            <Pagination
              page={page}
              changePage={changePage}
              totalPages={totalPages}
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export default Events;
