import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import PostService from "../../../API/PostService";
import { useFetching } from "../../hooks/useFetching";
import Loader from "../../ui/loader/Loader";
import "./postPage.css"; // Import the CSS

const PostPage = () => {
  const params = useParams();
  const [post, setPost] = useState({});

  // Fetching post by ID
  const [fetchPostById, isLoading, postError] = useFetching(async (id) => {
    const response = await PostService.getById(params.id);
    setPost(response);
  });

  useEffect(() => {
    fetchPostById(params.id);
  }, [params.id]);

  return (
    <div className='view-post'>
      <div className='post-page'>
        <h1 className='post-title'>Новость ID = {params.id}</h1>

        {isLoading ? (
          <div className='loader-container'>
            <Loader />
          </div>
        ) : (
          <div className='post-content'>
            {post.image && (
              <div className='post-image-container'>
                <img
                  src={`http://localhost:3001/${post.image}`}
                  alt={post.title}
                  className='post-image'
                />
              </div>
            )}
            <div className='post-text'>
              <h2 className='post-subtitle'>{post.description}</h2>
              <p className='post-body'>{post.body}</p>
            </div>
          </div>
        )}

        {postError && <p className='error-message'>Error: {postError}</p>}
      </div>
    </div>
  );
};

export default PostPage;
