import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import PostService from "../../../API/PostService";
import { useFetching } from "../../hooks/useFetching";
import Loader from "../../ui/loader/Loader";
import "./postPage.css";
import MyButton from "../../ui/button/MyButton";

const PostPage = () => {
  const params = useParams();
  const navigate = useNavigate();
  const [post, setPost] = useState({});
  const [isEditing, setIsEditing] = useState(false);
  const [loading, setLoading] = useState(true);
  const [postError, setPostError] = useState(null);

  const fetchPostById = async (id) => {
    try {
      const response = await PostService.getById(id);
      setPost(response);
    } catch (error) {
      setPostError(error.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPostById(params.id);
  }, [params.id]);

  const handleUpdatePost = async () => {
    try {
      await PostService.updatePost(params.id, post);
      setIsEditing(false);
    } catch (error) {
      setPostError(error.message);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setPost((prevPost) => ({
      ...prevPost,
      [name]: value,
    }));
  };

  return (
    <div className='view-post'>
      <div className='post-page'>
        {loading ? (
          <div className='loader-container'>
            <Loader />
          </div>
        ) : (
          <>
            {isEditing ? (
              <div className='edit-post'>
                <h2 className='post-title'>Редактировать пост</h2>
                <input
                  type='text'
                  name='title'
                  value={post.title}
                  onChange={handleChange}
                  className='edit-input'
                  placeholder='Заголовок'
                />
                <input
                  type='text'
                  name='description'
                  value={post.description}
                  onChange={handleChange}
                  className='edit-input'
                  placeholder='Описание'
                />
                <textarea
                  name='body'
                  value={post.body}
                  onChange={handleChange}
                  className='edit-textarea'
                  placeholder='Содержание'
                />
                <MyButton onClick={handleUpdatePost}>
                  Сохранить изменения
                </MyButton>
                <MyButton onClick={() => setIsEditing(false)}>Отмена</MyButton>
              </div>
            ) : (
              <div className='post-content'>
                <h1 className='post-title'>{post.title}</h1>
                <h2 className='post-subtitle'>{post.description}</h2>
                <p className='post-body'>{post.body}</p>
                {post.image && (
                  <div className='post-image-container'>
                    <img
                      src={`http://localhost:3001/${post.image}`}
                      alt={post.title}
                      className='post-image'
                    />
                  </div>
                )}
                <MyButton onClick={() => setIsEditing(true)}>
                  Редактировать
                </MyButton>
                <MyButton onClick={() => navigate(`/events`)}>Назад</MyButton>
              </div>
            )}
          </>
        )}

        {postError && <p className='error-message'>Error: {postError}</p>}
      </div>
    </div>
  );
};

export default PostPage;
